mapper.select:
select * from
hm_video v where
v.id = #{id}

select v.*,(case when g.groupId = 0 then '未分组' else g.title end) as groupTitle from hm_video v inner join hm_group g on v.groupId = g.id where v.passportId is null and g.type = #{type} and v.delType = 1
and v.groupId = #{groupId}
and (v.title like #{search})
order by v.createTime desc limit #{first},#{max}

		select v.*,(case when g.groupId = 0 then '未分组' else g.title end) as groupTitle
		from hm_video v inner join hm_group g
		on v.groupId = g.id where g.type = #{type} and v.delType = 1 and v.status = 7

		
			and v.groupId = #{groupId}
		
		
			and (v.title like #{search})
		

		order by v.createTime desc limit #{first},#{max}

select * from
hm_video v where
v.videoId = #{videoId}

select count(v.id) from hm_video v inner join hm_group g on v.groupId = g.id where v.passportId is null and g.type = #{type} and v.delType = 1
and v.groupId = #{groupId}
and (v.title like #{search})
